(136, 1, '2016-02-04 18:02:04', '2016-02-04 17:02:04', '<pre>public void ExecuteAdminScript(int id, int timeout = Timeout.Infinite)\n{\n    // Read connection info from the connections.config file\n    var allConnections = Config.CollectConnectionInfos();\n \n    // Read the V2 url\n    var baseurl = Api.Parameters.Read(283).Value.TrimEnd(&#039;/&#039;);\n \n    // Get credentials from connection\n    var userName = allConnections&#91;0&#93;.UserName;\n    var passWord = allConnections&#91;0&#93;.Password;\n    var cookieContainer = new CookieContainer();\n \n    // Create odata request\n    var request = (HttpWebRequest)WebRequest.Create(string.Format(&quot;{0}/api.svc/ExecuteAdminScript?scriptId={1}&amp;scriptParameterSetId=0&quot;,\n        baseurl, id));\n \n    request.Timeout = timeout;\n    request.ReadWriteTimeout = timeout;\n    request.CookieContainer = cookieContainer;\n    request.Method = &quot;GET&quot;;\n    request.Credentials = new NetworkCredential(userName, passWord);\n \n    // Read response\n    using (var response = (HttpWebResponse)request.GetResponse())\n    {\n        using (var stream = response.GetResponseStream())\n        {\n            var reader = new StreamReader(stream);\n            reader.ReadToEnd();\n        }\n    }\n}</pre>', 'How to trigger an adminscript via OData from within inca ?', '', 'publish', 'open', 'closed', '', 'how-to-trigger-an-adminscript-via-odata-from-within-inca', '', '', '2016-02-15 09:49:22', '2016-02-15 08:49:22', '', 134, 'http://tenforce.konstantinkharlov.com/questions/question/how-to-trigger-an-adminscript-via-odata-from-within-inca/?show_answer=136#answer_136', 0, 'answer', '', 0),
(137, 1, '2016-02-04 18:02:47', '2016-02-04 17:02:47', '<pre>public void ExecuteAdminScript(int id, int timeout = Timeout.Infinite)\n\n{\n\n    // Read connection info from the connections.config file\n\n    var allConnections = Config.CollectConnectionInfos();\n\n \n\n    // Read the V2 url\n\n    var baseurl = Api.Parameters.Read(283).Value.TrimEnd(&#039;/&#039;);\n\n \n\n    // Get credentials from connection\n\n    var userName = allConnections&#91;0&#93;.UserName;\n\n    var passWord = allConnections&#91;0&#93;.Password;\n\n    var cookieContainer = new CookieContainer();\n\n \n\n    // Create odata request\n\n    var request = (HttpWebRequest)WebRequest.Create(string.Format(&quot;{0}/api.svc/ExecuteAdminScript?scriptId={1}&amp;scriptParameterSetId=0&quot;,\n\n        baseurl, id));\n\n \n\n    request.Timeout = timeout;\n\n    request.ReadWriteTimeout = timeout;\n\n    request.CookieContainer = cookieContainer;\n\n    request.Method = &quot;GET&quot;;\n\n    request.Credentials = new NetworkCredential(userName, passWord);\n\n \n\n    // Read response\n\n    using (var response = (HttpWebResponse)request.GetResponse())\n\n    {\n\n        using (var stream = response.GetResponseStream())\n\n        {\n\n            var reader = new StreamReader(stream);\n\n            reader.ReadToEnd();\n\n        }\n\n    }\n\n}</pre>', 'How to trigger an adminscript via OData from within inca ?', '', 'inherit', 'closed', 'closed', '', '136-revision-v1', '', '', '2016-02-04 18:02:47', '2016-02-04 17:02:47', '', 136, 'http://tenforce.konstantinkharlov.com/136-revision-v1/', 0, 'revision', '', 0) ;
