ld_code, &quot;true&quot;);\n// date fields\nhandler.SetDate(date_field_code, DateTime.Now);\nhandler.SetDateTime(datetime_field_code, DateTime.Now);\n// value of a selection field\nhandler.SetIntByCode(single_field_code, &quot;SingleValue&quot;);\n// multiple values of a selection field\nhandler.SetMultipleInts(multiple_field_code, &quot;&quot;);\nhandler.SetMultipleIntsByCode(multiple_field_code, &quot;&quot;);\n\n// Save changes\nif (handler.IsModified &amp;&amp; !handler.Save(false))\n  &lt;span class=&quot;crayon-st&quot;&gt;throw&lt;/span&gt; &lt;span class=&quot;crayon-r&quot;&gt;new&lt;/span&gt; &lt;span class=&quot;crayon-e&quot;&gt;ScriptException&lt;/span&gt;&lt;span class=&quot;crayon-sy&quot;&gt;(&lt;span class=&quot;crayon-s&quot;&gt;&quot;Failed to save item (&quot;&lt;/span&gt; &lt;span class=&quot;crayon-o&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;crayon-v&quot;&gt;item&lt;/span&gt;&lt;span class=&quot;crayon-sy&quot;&gt;.&lt;/span&gt;&lt;span class=&quot;crayon-v&quot;&gt;Id&lt;/span&gt; &lt;span class=&quot;crayon-o&quot;&gt;+&lt;/span&gt; &lt;span class=&quot;crayon-s&quot;&gt;&quot;): &quot;&lt;/span&gt; &lt;span class=&quot;crayon-o&quot;&gt;+&lt;/span&gt;&lt;/span&gt; &lt;span class=&quot;crayon-v&quot;&gt;Api&lt;/span&gt;&lt;span class=&quot;crayon-sy&quot;&gt;.&lt;/span&gt;&lt;span class=&quot;crayon-v&quot;&gt;Items&lt;/span&gt;&lt;span class=&quot;crayon-sy&quot;&gt;.&lt;/span&gt;&lt;span class=&quot;crayon-v&quot;&gt;ErrorMessage&lt;/span&gt;&lt;span class=&quot;crayon-sy&quot;&gt;)&lt;/span&gt;&lt;span class=&quot;crayon-sy&quot;&gt;;&lt;/span&gt;</pre>', 'How to retrieve and update multiple fields of an item?', '', 'inherit', 'closed', 'closed', '', '320-revision-v1', '', '', '2016-02-23 09:58:57', '2016-02-23 08:58:57', '', 320, 'http://tenforce.konstantinkharlov.com/320-revision-v1/', 0, 'revision', '', 0),
